
#length

def m2ft(m):
    return m * 3.28084

def ft2m(ft):
    return ft / 3.28084

def cm2in(cm):
    return cm / 2.54

def in2cm(inch):
    return inch * 2.54

def km2mi(km):
    return km / 1.60934

def mi2km(mi):
    return mi * 1.60934

def cm2m(cm):
    return cm / 100

def m2km(m):
    return m / 1000

#weight

def kg2lb(kg):
    return kg * 2.20462

def lb2kg(lb):
    return lb / 2.20462

def g2oz(g):
    return g / 28.3495

def oz2g(oz):
    return oz * 28.3495

def g2kg(g):
    return g / 1000

def kg2g(kg):
    return kg * 1000


#time

def min2sec(minutes):
    return minutes * 60

def sec2min(seconds):
    return seconds / 60

def hr2min(hours):
    return hours * 60

def min2hr(minutes):
    return minutes / 60

def hr2sec(hours):
    return hours * 3600

def sec2hr(seconds):
    return seconds / 3600

def ms2sec(milliseconds):
    return milliseconds / 1000

def sec2ms(seconds):
    return seconds * 1000

def us2sec(microseconds):
    return microseconds / 1000000

def sec2us(seconds):
    return seconds * 1000000

def ns2sec(nanoseconds):
    return nanoseconds / 1000000000

def sec2ns(seconds):
    return seconds * 1000000000

def days2hr(days):
    return days * 24

def hr2days(hours):
    return hours / 24

def weeks2days(weeks):
    return weeks * 7

def days2weeks(days):
    return days / 7

def months2days(months):
    return months * 30.44  # Average days per month

def days2months(days):
    return days / 30.44

def years2days(years):
    return years * 365.25  # Average days per year

def days2years(days):
    return days / 365.25

#data

def BI2KB(bytes):
    return bytes / 1024

def KB2BI(kilobytes):
    return kilobytes * 1024

def BI2MB(bytes):
    return bytes / (1024 ** 2)

def MB2BI(megabytes):
    return megabytes * (1024 ** 2)

def BI2GB(bytes):
    return bytes / (1024 ** 3)

def GB2BI(gigabytes):
    return gigabytes * (1024 ** 3)

def BI2TB(bytes):
    return bytes / (1024 ** 4)

def TB2BI(terabytes):
    return terabytes * (1024 ** 4)

def BI2PB(bytes):
    return bytes / (1024 ** 5)

def PB2BI(petabytes):
    return petabytes * (1024 ** 5)

def KB2MB(kilobytes):
    return kilobytes / 1024

def MB2KB(megabytes):
    return megabytes * 1024

def KB2GB(kilobytes):
    return kilobytes / (1024 ** 2)

def GB2KB(gigabytes):
    return gigabytes * (1024 ** 2)

def MB2GB(megabytes):
    return megabytes / 1024

def GB2MB(gigabytes):
    return gigabytes * 1024

# Area Conversions
def sqm2sqft(sqm):
    return sqm * 10.7639

def sqft2sqm(sqft):
    return sqft / 10.7639

def sqm2sqcm(sqm):
    return sqm * 10000

def sqcm2sqm(sqcm):
    return sqcm / 10000



